const math = require("mathjs");

const generate = (usePredefinedValues = 0) => {
    const unitSystems = {
        si: {
            speed: 'm/s',
            distance: 'm',
            time: 's'
        },
        uscs: {
            speed: 'ft/s',
            distance: 'ft',
            time: 's'
        }
    };

    // 1. Dynamic Parameter Selection
    const selectedSystem = math.randomInt(0, 2) === 0 ? 'si' : 'uscs';
    const units = unitSystems[selectedSystem];

    // 2. Value Generation
    let speed, time;
    if (usePredefinedValues === 0) {
        speed = selectedSystem === 'si' ? math.round(math.random(10, 30), 1) : math.round(math.random(30, 100), 1); // speed range in m/s or ft/s
        time = math.round(math.random(5, 20), 1); // time range in seconds
    } else {
        speed = selectedSystem === 'si' ? 20 : 60; // example predefined values
        time = 10;
    }

    // 3. Computation Logic
    const distance = speed * time;

    // Return structured data
    return {
        params: {
            speed: speed,
            time: time,
            unitsSpeed: units.speed,
            unitsTime: units.time,
            unitsDistance: units.distance
        },
        correct_answers: {
            distance: math.round(distance, 3)
        },
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };
